import { useState, useEffect } from "react";

const Index = () => {
  const [apiResponse, setApiResponse] = useState();

  const callAPI = async () => {
    fetch("http://localhost:4000/testAPI")
      .then((res) => res.text())
      .then((res) => setApiResponse({ apiResponse: res }));
  };

  useEffect(() => {
    callAPI();
  }, []);

  return (
    <div>
      <h1>{{ apiResponse }}</h1>
    </div>
  );
};

export default Index;
